mapper.update:
update cert
set cert_confirm_time = systimestamp
where cert_email = #{certEmail}

mapper.delete:
delete cert where cert_email in( select TMP.cert_email from( select cert.*, extract(day from (systimestamp - cert_time)) * 24 * 60 + extract(hour from (systimestamp - cert_time)) * 60 + extract(minute from (systimestamp - cert_time)) + extract(second from (systimestamp - cert_time)) / 60 as diff from cert )TMP where
(cert_confirm_time is null and diff >= ${expireMinutes}) or (cert_confirm_time is not null and diff >= ${expireAccept})
)
